    public function validate($clientAccountBilling_form, $clientAccount) {

        $errors = array();

        $action = $clientAccountBilling_form['action'];

        $billingDate = $clientAccountBilling_form['billingDate'];

        // d = delete
        if($action !== 'd') {
            $clientAccountBillingExist = $this->getEntityManager()->getRepository(ClientAccountBillingEntity::class)
                ->createQueryBuilder('cmr')
                ->where('cmr.id !=  :id')
                ->andWhere('cmr.isDeleted =  0')
                ->andWhere('cmr.clientAccount = :clientAccountId')
                ->andWhere('cmr.billingDate BETWEEN :startDate AND :endDate')
                ->setParameters(array(
                    'clientAccountId' => base64_decode($clientAccountBilling_form['clientAccount']),
                    'startDate' => date("Y-m-01",strtotime($clientAccountBilling_form['billingDate'])) . ' 00:00:00',
                    'endDate' => date("Y-m-t",strtotime($clientAccountBilling_form['billingDate'])) . ' 23:59:59',
                    'id' => $clientAccountBilling_form['id']
                ))
                ->getQuery()->getResult();

            if($clientAccountBillingExist) {
                $errors[] = 'Account already has a billing this month.';
            }

            if(strtotime($clientAccount->getLastBillingDateAsString()) > strtotime($clientAccountBilling_form['billingDate'])){
                $errors[] = 'Invalid  billing date.';

            }

            
        }

        return $errors;
    }

    public function ajax_list(array $get, $userData){

        $columns = array(
            array('cmr.`id`', "cmr.`id`"),
            array('DATE_FORMAT(cmr.`billing_date`,"%m/%d/%Y")', 'DATE_FORMAT(cmr.`billing_date`,"%m/%d/%Y")', 'billingDate'),
            array('cmr.`status`', 'cmr.`status`', 'status'),
            array('cmr.`billed_amount`', 'cmr.`billed_amount`', 'billedAmount'),
            array('DATE_FORMAT(cmr.`due_date`,"%m/%d/%Y")','DATE_FORMAT(cmr.`due_date`,"%m/%d/%Y")', 'dueDate'),
        );
        $asColumns = array();

        $select = "SELECT";
        $from = "FROM `client_account_billing` cmr";
        $sqlWhere = " WHERE cmr.`is_deleted` = 0";
        $joins = "";
        $groupBy = "";
        $orderBy = "";
        $limit = "";
        $stmtParams = array();

        foreach($columns as $key => $column) {
            $select .= ($key > 0 ? ', ' : ' ') . $column[1] . (isset($column[2]) ? ' AS ' . $column[2] : '');
        }


        if($userData['type'] != 'Super Admin' || $get['clientAccountId']){

            $sqlWhere .= " AND cmr.`client_account_id` = :clientAccountId";
            $stmtParams['clientAccountId'] = base64_decode($get['clientAccountId']);
        }

        /*
         * Ordering
         */
        foreach($get['columns'] as $key => $column) {
            if($column['orderable']=='true') {
                if(isSet($get['order'])) {
                    foreach($get['order'] as $order) {
                        if($order['column']==$key) {
                            $orderBy .= (!empty($orderBy) ? ', ' : 'ORDER BY ') . $columns[$key][0] . (!empty($order['dir']) ? ' ' . $order['dir'] : '');
                        }
                    }
                }
            }
        }

        /*
         * Filtering
         */
        if(isset($get['search']) && $get['search']['value'] != ''){
            $aLikes = array();
            foreach($get['columns'] as $key => $column) {
                if($column['searchable']=='true') {
                    $aLikes[] = $columns[$key][0] . ' LIKE :searchValue';
                }
            }
            foreach($asColumns as $asColumn) {
                $aLikes[] = $asColumn . ' LIKE :searchValue';
            }
            if(count($aLikes)) {
                $sqlWhere .= (!empty($sqlWhere) ? ' AND ' : 'WHERE ') . '(' . implode(' OR ', $aLikes) . ')';
                $stmtParams['searchValue'] = "%" . $get['search']['value'] . "%";
            }
        }

        /* Set Limit and Length */
        if(isset( $get['start'] ) && $get['length'] != '-1'){
            $limit = 'LIMIT ' . (int)$get['start'] . ',' . (int)$get['length'];
        }

        $sql = "$select $from $joins $sqlWhere $groupBy $orderBy";
        $query = $this->getEntityManager()->getConnection()->prepare($sql);
     

        foreach($stmtParams as $k => $v){
            $query->bindValue($k, $v);

        }
        $res = $query->executeQuery();
        $result_count = $res->fetchAllAssociative();
        $sql = "$select $from $joins $sqlWhere $groupBy $orderBy $limit";
        $query = $this->getEntityManager()->getConnection()->prepare($sql);
        foreach($stmtParams as $k => $v){
            $query->bindValue($k, $v);

        }
        $res = $query->executeQuery();
        $result = $res->fetchAllAssociative();

        /* Data Count */
        $recordsTotal = count($result_count);

        /*
         * Output
         */
        $output = array(
            "draw" => intval($get['draw']),
            "recordsTotal" => $recordsTotal,
            "recordsFiltered" => $recordsTotal,
            "data" => array()
        );

        $url = $get['url'];
        $formUrl = '';
        $printUrl = 'client_account/print/temporary_receipt/';
        $hasUpdate = false;
        $hasPrint = false;
        $hasDetails = false;

        
        if($userData['type'] == 'Super Admin'  || in_array('Client Details Account Details Billing Update', $userData['accesses'])){
            $hasUpdate = true;
        }

        if($userData['type'] == 'Super Admin'  || in_array('Client Details Account Details Billing Print', $userData['accesses'])){
            $hasPrint = true;
        }

        $action = '';


        foreach($result as $k => $row) {
            $id = base64_encode($row['id']);
         
            $action .= $hasUpdate ? "<a class='action-button-style btn btn-primary  href-modal' href='javascript:void(0)' data-id='".$id."' data-action='u' data-type='billing'>Update</a>" : "";
            $action .= $hasPrint ? " <a class='action-button-style btn btn-primary'  href='".$url.$printUrl.$id."' target='_blank'>Download</a>" : "";
            $values = array(
                $row['billingDate'],
                $row['status'],
                $row['billedAmount'],
                $row['dueDate'],
                $k === array_key_first($result) ? $action : ''
                
            );

            $output['data'][] = $values;
        }

        unset($result);

        return $output;
    }

    public function autocompleteSuggestions($q, $userData) {

        $stmtParams = array(
            'q' => "%" . $q['query'] . "%"
        );
        $andWhere = '';

        if($userData['type'] != 'Super Admin'){

             $andWhere.= ' WHERE u.branch_id = :branchId'; 
             $stmtParams['branchId'] = $userData['branchId'];   
        }

        
        $query = $this->getEntityManager()->getConnection()->prepare("
            SELECT
                u.`id`,
                CONCAT(u.`first_name`, ' ', u.`last_name`) AS data,
                CONCAT(u.`first_name`, ' ', u.`last_name`) AS value
            FROM `client` u
            $andWhere
            AND u.`is_deleted` != 1
            AND CONCAT(u.`first_name`, ' ', u.`last_name`) LIKE :q
            ORDER BY u.`first_name`
            LIMIT 0,20
        ");

        foreach($stmtParams as $k => $v){
            $query->bindValue($k, $v);

        }
       
        $res = $query->executeQuery();
        $result = $res->fetchAllAssociative();
        return $result;
    }

