   public function consumptionData($date, $purok, $userData){
   

        $stmtParams = array();
        $sqlWhere = '';

        if($userData['type'] != 'Super Admin'){

             $stmtParams['branchId'] = base64_decode($userData['branchId']);   
        }

        if($date){
            $sqlWhere.= ' AND cmr.`billing_date` BETWEEN "' .date("Y-m-01",strtotime(str_replace('-', '/', $date))) . ' 00:00:00" AND "'.date("Y-m-t",strtotime(str_replace('-', '/', $date))) . ' 23:59:59"';    
        }
        if($purok &&  $purok != 'Y' ){
            $sqlWhere.= ' AND p.`id` = :purok';
            $stmtParams['purok'] = $purok;   
        }

        $query = $this->getEntityManager()->getConnection()->prepare("
            SELECT 
                CONCAT(c.`first_name`, ' ', c.`last_name`, ' - ', cm.`account_serial_no`) AS account,
                p.`description` AS purok,
                cmr.`billed_amount` AS billedAmount,
                cmr.`consume` AS consume
            FROM `client_account_billing` cmr
            LEFT JOIN `client_account` cm ON cm.`id` = cmr.`client_account_id` 
            LEFT JOIN `client` c ON c.`id` = cm.`client_id` 
            LEFT JOIN `purok` p ON p.`id` = cm.`purok_id` 
            WHERE cmr.`is_deleted` = 0 
            AND c.branch_id = :branchId 
            $sqlWhere 
            ORDER BY p.`description` ASC
        ");

        foreach($stmtParams as $k => $v){
            $query->bindValue($k, $v);

        }
       
        $res = $query->executeQuery();
        $result = $res->fetchAllAssociative();
        return $result;
   }

   public function get_pending_payment(){

        $query = $this->getEntityManager()->getConnection()->prepare("
                SELECT 
                    CONCAT(c.`first_name`, ' ', c.`last_name`) AS fullName,
                    b.`id` AS branchId,
                    cm.`account_serial_no` AS serialNo,
                    cmr.previousBilling AS previousBilling,
                    cmr.presentBilling AS presentBilling,
                    cmr.consume AS consume, 
                    DATE_FORMAT(cmr.billingDate, '%m/%d/%Y') AS billingDate,
                    DATE_FORMAT(cmr.dueDate, '%m/%d/%Y') AS dueDate,
                    cm.`remaining_balance` AS remainingBalance,
                    cmr.billedAmt AS billedAmount,
                    (cm.`remaining_balance` +  cmr.billedAmt ) AS totalBalance,
                    cmr.amountPerCubic AS amountPerCubic,
                    cm.`id`,
                    co.`description` AS company,
                    co.`id` AS companyId,
                    co.`address` AS companyAddress,
                    p.`description` AS purok,
                    c.`contact_no` AS contactNo,
                    DATE_FORMAT(cmc.firstPendingPayment, '%m/%d/%Y') AS firstPendingPayment  
                FROM `client_account` cm 
                LEFT JOIN `client` c ON c.`id` = cm.`client_id` 
                LEFT JOIN `purok` p ON p.`id` = cm.`purok_id` 
                LEFT JOIN `branch` b ON b.`id` = c.`branch_id`
                LEFT JOIN `company` co ON co.`id` = b.`company_id` 
                LEFT JOIN ( 
                    SELECT 
                        cmr.`status` AS status,
                        cmr.`previous_billing` AS previousBilling,
                        cmr.`present_billing` AS presentBilling,
                        cmr.`consume` AS consume,
                        cmr.`billing_date` AS billingDate,
                        cmr.`due_date` AS dueDate,
                        cmr.`billed_amount` AS billedAmt,
                        cmr.`amount_per_cubic` AS amountPerCubic,
                        cmr.`id` AS cmrId, cmr.`client_account_id` AS clientAccountId 
                    FROM `client_account_billing` cmr 
                    WHERE cmr.`status` = 'Pending Payment' 
                    AND cmr.`billing_date` IN ( 
                        SELECT 
                            MAX(billing_date) 
                            FROM `client_account_billing` s 
                        WHERE s.`status` = 'Pending Payment' 
                        AND s.`is_deleted` = 0 
                        GROUP BY s.`client_account_id` ) 
                    AND cmr.`is_deleted` = 0 
                    GROUP BY cmr.`client_account_id` ) cmr ON cm.`id` = cmr.clientAccountId 
                LEFT JOIN ( 
                    SELECT 
                        cmc.`due_date` AS firstPendingPayment,
                        cmc.`client_account_id` AS cclientAccountId 
                    FROM `client_account_billing` cmc 
                    WHERE cmc.`status` = 'Pending Payment' 
                    AND cmc.`billing_date` IN ( 
                        SELECT 
                            MIN(billing_date) 
                            FROM `client_account_billing` s 
                        WHERE s.`status` = 'Pending Payment' 
                        AND s.`is_deleted` = 0 
                        GROUP BY s.`client_account_id` ) 
                    AND cmc.`is_deleted` = 0 
                    GROUP BY cmc.`client_account_id` ) cmc ON cm.`id` = cmc.cclientAccountId   
                WHERE cm.`is_deleted` = 0 
                AND cm.`status` = 'Active' 
                AND cmr.cmrId IS NOT NULL 
                AND c.`is_deleted` = 0
                AND  cmr.dueDate <= DATE(NOW() - INTERVAL 3 DAY) 

            
        ");
       
        $res = $query->executeQuery();
        $result = $res->fetchAllAssociative();
        return $result;

    }

    public function monthBill($id){
        
        $lastDayOfMonth =  date('t');
        $yearAndMonth = date('Y-m');
        $endDate  = $yearAndMonth . '-' . $lastDayOfMonth;
        $startDate =  $yearAndMonth .'-01';
        
        $query = $this->getEntityManager()->getConnection()->prepare("
                SELECT 
                    cab.`id`
                FROM `client_account_billing` cab
                WHERE cab.`billing_date`  BETWEEN '".$startDate." 00:00:00' AND '".$endDate." 23:59:59'
                AND cab.`is_deleted` != 1
                AND cab.`client_account_id` = '".$id."'
            
        ");
       
        $res = $query->executeQuery();
        $result = $res->fetchAllAssociative();
        return isset($result[0]) ? $result[0] : [];

    }

    public function billing_report($dateFrom, $dateTo, $status, $userData){
       
        $stmtParams = array();
        $andWhere = ' ';

        if($userData['type'] != 'Super Admin'){
             $stmtParams['branchId'] = base64_decode($userData['branchId']);   
        }


    
       if($dateFrom != 'NULL' && $dateTo != 'NULL'){
            $andWhere .= ' AND cmb.`billing_date` BETWEEN "' . date('Y-m-d', strtotime(str_replace('-', '/', $dateFrom))) . ' 00:00:00" AND "' .  date('Y-m-d', strtotime(str_replace('-', '/', $dateTo))) . ' 23:59:59"';   
       } else if( $dateFrom != 'NULL'){
            $andWhere .= ' AND cmb.`billing_date` >= "' . date('Y-m-d', strtotime(str_replace('-', '/', $dateFrom))) . ' 00:00:00"';
       } else if($dateTo != 'NULL') {
            $andWhere .= ' AND cmb.`billing_date` <= "' .   date('Y-m-d', strtotime(str_replace('-', '/', $dateTo))) . ' 23:59:59"';
       }

       if($status && $status != 'All'){
            $andWhere .= ' AND cmb.`status` = :status';
            $stmtParams['status'] = $status;  
        }

        $query = $this->getEntityManager()->getConnection()->prepare("
                SELECT 
                
                    CONCAT(c.`first_name`, ' ', c.`last_name`) AS fullName,
                    cm.`description` AS accountName,
                    cmb.`status` AS status,
                    cm.`remarks` AS remarks,
                    cmb.`billed_amount` AS billingAmount,
                    DATE_FORMAT(cmb.`billing_date`, '%m/%d/%Y') AS billingDate
                FROM `client_account_billing` cmb 
                LEFT JOIN `client_account` cm ON cm.`id` = cmb.`client_account_id` 
                LEFT JOIN `client` c ON c.`id` = cm.`client_id` 
                LEFT JOIN `branch` b ON b.`id` = c.`branch_id`
                LEFT JOIN `company` co ON co.`id` = b.`company_id`   
                WHERE cmb.`is_deleted` = 0 
                AND c.`branch_id` = :branchId 
                AND c.`is_deleted` = 0
                AND cm.`is_deleted` = 0
                AND cm.`status` = 'Active'
                ".$andWhere."
                ORDER BY cmb.`billing_date` asc
        ");

        foreach($stmtParams as $k => $v){
            $query->bindValue($k, $v);

        }
       
        $res = $query->executeQuery();
        $result = $res->fetchAllAssociative();
        return $result;

    }

    

   
